mapper.update:
UPDATE students
SET name = #{name}, kanaName = #{kanaName}, nickname = #{nickname},
email = #{email}, area = #{area}, age = #{age}, sex = #{sex}, remark = #{remark}
WHERE id = #{id}

UPDATE students_courses
SET course_name = #{courseName}, course_start_at = #{courseStartAt}, course_end_at =
#{courseEndAt}
WHERE id = #{id}

UPDATE enrollment_status
SET status = #{status}
WHERE course_id = #{courseId}

mapper.select:
SELECT id, course_id as courseId, status
FROM enrollment_status
WHERE course_id = #{courseId}

SELECT DISTINCT s.id, s.name, s.kanaName, s.nickname, s.email, s.area, s.age, s.sex, s.remark, s.deleted FROM students s LEFT JOIN students_courses sc ON s.id = sc.student_id LEFT JOIN enrollment_status es ON sc.id = es.course_id WHERE (s.deleted IS NULL OR s.deleted = 0)
AND s.name LIKE CONCAT('%', #{name}, '%')
AND s.area LIKE CONCAT('%', #{area}, '%')
AND sc.course_name = #{courseName}
AND es.status = #{enrollmentStatus}
ORDER BY s.id
